import React, { useState } from "react";


const EmployeeForm = () => {

  const initialState = {
    firstName: "",
    lastName: "",
    emailId: "",
  }

  const [employee, setEmployee] = useState(initialState);

  const handleInputChange = (event) => {
    setEmployee({
      ...employee,
      [event.target.name]: event.target.value
    });
  }

  const enviarDatos = (event) => {
    event.preventDefault();
    event.target.reset();
  }

  return (
    <div className="mt-3">

      <div className="mb-4 text-center col-auto"><h3>Agregar empleado</h3></div>

      <div className="row h-100 justify-content-center align-items-center">
        <div className="col-auto">
          <form action="" className="form-horizontal justify-content-center" onSubmit={enviarDatos}>

            <div className="form-group ">
              <div className="mb-2">
                <input type="text" className="form-control" name="firstName" placeholder="First Name" 
                  value={employee.firstName} onChange={handleInputChange}
                />
              </div>
            </div>

            <div className="form-group">
              <div className="mb-2">
                <input type="text" className="form-control" name="lastName" placeholder="Last Name" 
                  value={employee.lastName} onChange={handleInputChange}
                />
              </div>
            </div>

            <div className="form-group">
              <div className="mb-2">
                <input type="email" className="form-control" name="emailId" placeholder="Email" 
                  value={employee.emailId} onChange={handleInputChange}
                />
              </div>
            </div>

            <div className="form-group">
              <div className="text-center">
                <button type="submit" className="btn btn-primary">Agregar</button>
              </div>
              <div className="text-center">
                <button className="btn btn-primary">Agregar</button>
              </div>
            </div>

          </form>
        </div>
      </div>

    </div>
  );
}

export default EmployeeForm;
