import React, { useState } from "react";


const EmployeeForm = () => {

  const initialState = {
    firstName: "",
    lastName: "",
    email: "",
  }

  const [employee, setEmployee] = useState(initialState);

  const changeFirstNameHandler = (event) => {
    setEmployee({
      ...employee,
      firstName: event.target.value
    });
  }

  const changeLastNameHandler = (event) => {
    setEmployee({
      ...employee,
      lastName: event.target.value
    });
  }

  const changeEmailHandler = (event) => {
    setEmployee({
      ...employee,
      email: event.target.value
    });
  }

  return (
    <div className="mt-3">

      <div className="mb-4 text-center col-auto"><h3>Agregar empleado</h3></div>

      <div className="row h-100 justify-content-center align-items-center">
        <div className="col-auto">
          <form action="" className="form-horizontal justify-content-center">

            <div className="form-group ">
              <div className="mb-2">
                <input type="text" className="form-control" placeholder="First Name" 
                  value={employee.firstName} onChange={() => {changeFirstNameHandler()}}
                />
              </div>
            </div>

            <div className="form-group">
              <div className="mb-2">
                <input type="text" className="form-control" placeholder="Last Name" 
                  value={employee.lastName} onChange={() => {changeLastNameHandler()}}
                />
              </div>
            </div>

            <div className="form-group">
              <div className="mb-2">
                <input type="email" className="form-control" placeholder="Email" 
                  value={employee.email} onChange={() => {changeEmailHandler()}}
                />
              </div>
            </div>

            <div className="form-group">
              <div className="text-center">
                <button type="submit" className="btn btn-primary">Agregar</button>
              </div>
            </div>

          </form>
        </div>
      </div>

    </div>
  );
}

export default EmployeeForm;
